void dfs(Node *u, bool init = true) {
    if (init) {
        u->dep = 1;
        u->f[0] = u;
    }

    static int dfsClock = 0;
    u->dfn = ++dfsClock;

    for (int i = 1; i f[i] = u->f[i - 1]->f[i - 1];

    for (Edge *e = u->e; e; e = e->next) if (e->v != u->f[0]) {
        e->v->f[0] = u;
        e->v->dep = u->dep + 1;
        dfs(e->v, false);
    }
}

Node *lca(Node *u, Node *v) {
    if (u->dep dep) std::swap(u, v);

    for (int i = MAXN_LOG - 1; ~i; i--) {
        if (u->f[i]->dep >= v->dep) u = u->f[i];
    }

    for (int i = MAXN_LOG - 1; ~i; i--) {
        if (u->f[i] != v->f[i]) {
            u = u->f[i];
            v = v->f[i];
        }
    }

    return u == v ? u : u->f[0];
}

