            if (val == l && val == r) return new Node(cnt + 1);
            pushDown();
            int mid = l + ((r - l) >> 1);
            if (val insert(l, mid, val), rc);
            else return new Node(lc, rc->insert(mid + 1, r, val));
        }

        int rank() const {
            return lc ? lc->cnt : 0;
        }
    } *root[MAXN];
    int n;

    PSegT() : root() {}

    void build(int *a, int n) {
