            if (dcmp((*u)->y, y) >= 0) return splay(*u);
            (*u)->y = y;
        } else {
            (*u) = new (_curr++) Node(fa, x, y);

            if ((*u)->relation()) {
                (*u)->succ = fa->succ;
                (*u)->pred = fa;
                if (fa->succ) fa->succ->pred = *u;
                fa->succ = *u;
